mapper.select:
SELECT

FROM med_record r
LEFT JOIN med_plan p ON r.plan_id = p.id
WHERE p.user_id = #{userId} AND p.deleted_at IS NULL

    AND r.id = #{id}



        AND r.plan_id = #{planId}


        AND r.`status` = #{status}


        AND r.`date` >= #{startDate}


        AND r.`date` <= #{endDate}


ORDER BY r.`date` DESC, r.`time` ASC

    LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM med_record r
LEFT JOIN med_plan p ON r.plan_id = p.id
WHERE p.user_id = #{userId} AND p.deleted_at IS NULL

    AND r.id = #{id}



        AND r.plan_id = #{planId}


        AND r.`status` = #{status}


        AND r.`date` >= #{startDate}


        AND r.`date` <= #{endDate}

mapper.update:
UPDATE med_record
`status` = #{status}, action_at = #{actionAt}, note = #{note}, updated_at = NOW()
WHERE id = #{id}
